mapper.select:
SELECT TB.*, SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT FROM ( SELECT b.FLOOR_SEQ, b.FLOOR_NAME, b.FLOOR_INFO, a.CENTER_ID , a.CENTER_NM , CASE WHEN length(FLOOR_MAP) > 0 THEN FLOOR_MAP ELSE 'no_image.gif' END FLOOR_MAP, fn_DetailCodeNm(b.FLOOR_INFO) FLOOR_INFO_TXT, SEAT_CNT, MEET_CNT, FLOOR_USEYN, FLOOR_PART, DATE_FORMAT(b.UPDATE_DATE,'%Y-%m-%d %H:%i:%s') UPDATE_DATE, b.UPDATE_ID FROM tb_centerinfo a, tb_floorinfo b WHERE a.CENTER_ID = b.CENTER_ID
AND ( b.FLOOR_NAME LIKE CONCAT('%',#{params.searchKeyword},'%') OR a.CENTER_NM LIKE CONCAT('%',#{params.searchKeyword},'%') )
AND a.CENTER_ID = #{params.centerId}
AND b.FLOOR_USEYN = #{params.floorUseyn}
ORDER BY b.FLOOR_SEQ DESC ) TB ORDER BY TB.FLOOR_SEQ DESC LIMIT ${params.firstIndex}, ${params.recordCountPerPage}

mapper.update:

 UPDATE  tb_floorinfo SET CENTER_ID = #{centerId} 
                          , FLOOR_INFO = #{floorInfo,jdbcType=VARCHAR} 
                          , FLOOR_NAME = #{floorName,jdbcType=VARCHAR}
                          , FLOOR_USEYN = #{floorUseyn,jdbcType=VARCHAR}
                          , FLOOR_PART = #{floorPart,jdbcType=VARCHAR} 

                           , FLOOR_MAP = #{floorMap,jdbcType=VARCHAR}


                           , FLOOR_MAP1 = #{floorMap1,jdbcType=VARCHAR}

                          , UPDATE_DATE = NOW()
                          , UPDATE_ID = #{userId,jdbcType=VARCHAR}
WHERE FLOOR_SEQ = #{floorSeq}
