mapper.select:
SELECT TB.* FROM ( SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, ROW_NUMBER() OVER (ORDER BY COM_CODE DESC) AS RNUM, a.COM_CODE, a.COM_NUMBER, a.COM_NAME, COM_BUSCONDITION, COM_ITEM, COM_CEO_NAME, CASE WHEN LEN(COM_LOGO) > 0 THEN COM_LOGO ELSE 'no_image.gif' END COM_LOGO, dbo.FN_DETAILCODENM(a.COM_GUBUN) COM_GUBUN_Txt, COM_TEL, COM_FAX, COM_ZIPCODE, COM_ADDR1, COM_ADDR2, COM_HOMEPAGE, COM_STATE, FORMAT(a.COM_UPDATE,'yyyy-MM-dd HH:mm:ss') COM_UPDATE, a.COM_UPDATEID, b.CODE_NM, c.CENTER_NM, d.FLOOR_NAME, a.TENN_USEYN, CASE a.TENN_USEYN WHEN 'Y' THEN (SELECT ISNULL(SUM(TENN_REC_NOW_CNT),0) FROM tb_compay_tennant e WHERE TENN_REC_END ='Y' AND a.COM_CODE = e.COM_CODE ) ELSE '사용안함' END TENN_INFO , a.COM_PLAY_FLOOR, dbo.FN_FLOORNM(a.COM_PLAY_FLOOR) FLOOR_NM, a.FLOOR_SEQ
FROM TB_COMPANYINFO a, LETTCCMMNDETAILCODE b, TB_CENTERINFO c, TB_FLOORINFO d WHERE a.COM_STATE = b.CODE AND a.CENTER_ID = c.CENTER_ID AND a.FLOOR_SEQ = d.FLOOR_SEQ
AND (a.COM_NAME LIKE CONCAT('%',#{params.searchKeyword},'%') OR a.COM_CEO_NAME LIKE CONCAT('%',#{params.searchKeyword},'%') )
AND a.CENTER_ID = #{params.searchCenter}
AND a.FLOOR_SEQ = #{params.searchFloorSeq}
AND a.COM_CODE != 'C_00000004'
) TB WHERE RNUM BETWEEN #{params.firstIndex} + 1 AND #{params.firstIndex} + #{params.recordCountPerPage} ORDER BY TB.COM_CODE DESC

mapper.update:

 UPDATE  TB_COMPANYINFO SET COM_NAME = #{comName} 
                          , COM_NUMBER = #{comNumber,jdbcType=VARCHAR} 
                          , COM_GUBUN = #{comGubun,jdbcType=VARCHAR}
                          , COM_BUSCONDITION = #{comBuscondition,jdbcType=VARCHAR}
                          , COM_ITEM = #{comItem,jdbcType=VARCHAR}
                          , COM_CEO_NAME = #{comCeoName,jdbcType=VARCHAR} 
                          , COM_TEL = #{comTel,jdbcType=VARCHAR} 
                          , COM_FAX = #{comFax,jdbcType=VARCHAR} 
                          , COM_ZIPCODE = #{comZipcode,jdbcType=VARCHAR} 
                          , COM_ADDR1 = #{comAddr1,jdbcType=VARCHAR} 
                          , COM_ADDR2 = #{comAddr2,jdbcType=VARCHAR} 
                          , COM_HOMEPAGE = #{comHomepage,jdbcType=VARCHAR} 
                          , COM_STATE = #{comState,jdbcType=VARCHAR} 
                          , TENN_USEYN = #{tennUseyn,jdbcType=VARCHAR} 

                           , COM_LOGO = #{comLogo,jdbcType=VARCHAR}

                          , COM_UPDATE = GETDATE()
                          , COM_UPDATEID = #{userId,jdbcType=VARCHAR}
                          , COM_MEMO =  #{comMemo, jdbcType=VARCHAR}
                          , CENTER_ID = #{centerId, jdbcType=VARCHAR}

                          , FLOOR_SEQ = #{floorSeq, jdbcType=VARCHAR}


                          , COM_PLAY_FLOOR = #{comPlayFloor, jdbcType=VARCHAR}

WHERE COM_CODE = #{comCode}
