mapper.select:
SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, TB.* FROM ( SELECT b.USER_ID, b.USER_NAME, b.USER_NO, b.COM_CODE, b.USER_RANK, b.USER_POSITION, b.USER_EMAIL, b.USER_TEL, b.USER_STATE, b.USER_CELLPHONE, DATE_FORMAT(b.USER_UPDATE,'%Y-%m-%d %H:%i:%s') USER_UPDATE, b.USER_UPDATEID, c.CODE_NM, a.COM_NAME FROM TB_COMPANYINFO a, TB_USERINFO b, LETTCCMMNDETAILCODE c, TB_CENTERINFO d, TB_FLOORINFO e WHERE a.COM_CODE = b.COM_CODE AND b.USER_STATE = c.CODE AND a.CENTER_ID = d.CENTER_ID AND d.CENTER_ID = e.CENTER_ID
AND b.COM_NAME LIKE CONCAT('%',#{params.searchKeyword},'%')
AND (b.USER_NO LIKE CONCAT('%',#{params.searchKeyword},'%') OR b.USER_ID LIKE CONCAT('%',#{params.searchKeyword},'%') OR b.USER_NAME LIKE CONCAT('%',#{params.searchKeyword},'%') )
AND a.COM_CODE = #{params.comCode}
AND a.COM_CODE = #{params.comCode}
AND d.CENTER_ID = #{params.searchCenter}
AND e.FLOOR_SEQ = #{params.searchFloorSeq}
ORDER BY b.USER_UPDATE DESC ) TB ORDER BY TB.USER_UPDATE DESC LIMIT ${params.firstIndex}, ${params.recordCountPerPage}

mapper.update:
        UPDATE  TB_USERINFO SET USER_ID = #{userId,jdbcType=VARCHAR} 
                                , USER_NAME = #{userName} 
                                , COM_CODE = #{comCode}
                                , USER_RANK = #{userRank,jdbcType=VARCHAR}
                                , USER_POSITION = #{userPosition,jdbcType=VARCHAR} 
                                , USER_EMAIL = #{userEmail,jdbcType=VARCHAR} 
                                , USER_STATE = #{userState,jdbcType=VARCHAR} 
                                , USER_CELLPHONE = #{userCellphone,jdbcType=VARCHAR} 
                                , USER_TEL = #{userTel,jdbcType=VARCHAR} 
                                , USER_UPDATE = NOW()
                                , USER_UPDATEID = #{updateId,jdbcType=VARCHAR}
	    WHERE USER_NO = #{userNo} ;

	    UPDATE TB_EMPINFO SET  EMPID = #{userId,jdbcType=VARCHAR} 
                                , EMPNAME = #{userName} 
                                , COM_CODE = #{comCode}
                                , EMPGRAD = #{userRank,jdbcType=VARCHAR}
                                , EMPJIKW = #{userPosition,jdbcType=VARCHAR} 
                                , USER_EMAIL = #{userEmail,jdbcType=VARCHAR} 
                                , USER_STATE = #{userState,jdbcType=VARCHAR} 
                                , EMPHANDPHONE = #{userCellphone,jdbcType=VARCHAR} 
                                , EMPTELPHONE = #{userTel,jdbcType=VARCHAR}
                                , UPDATE_DATE = NOW()
	    WHERE EMPNO = #{userNo} ;
