mapper.select:
SELECT TB.* FROM ( SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, ROW_NUMBER() OVER (ORDER BY b.USER_UPDATE DESC) AS RNUM, b.USER_ID, b.USER_NAME, b.USER_NO, b.COM_CODE, b.USER_RANK, b.USER_POSITION, b.USER_EMAIL, b.USER_TEL, b.USER_STATE, b.USER_CELLPHONE, FORMAT(b.USER_UPDATE,'yyyy-MM-dd HH:mm:ss') USER_UPDATE, b.USER_UPDATEID, c.CODE_NM, a.COM_NAME FROM TB_COMPANYINFO a, TB_USERINFO b, LETTCCMMNDETAILCODE c, TB_CENTERINFO d, TB_FLOORINFO e WHERE a.COM_CODE = b.COM_CODE AND b.USER_STATE = c.CODE AND a.CENTER_ID = d.CENTER_ID AND a.FLOOR_SEQ = e.FLOOR_SEQ
AND b.COM_NAME LIKE CONCAT('%',#{params.searchKeyword},'%')
AND (b.USER_NO LIKE CONCAT('%',#{params.searchKeyword},'%') OR b.USER_ID LIKE CONCAT('%',#{params.searchKeyword},'%') OR b.USER_NAME LIKE CONCAT('%',#{params.searchKeyword},'%') )
AND a.COM_CODE = #{params.comCode}
AND d.CENTER_ID = #{params.searchCenter}
AND e.FLOOR_SEQ = #{params.searchFloorSeq}
) TB WHERE RNUM BETWEEN #{params.firstIndex} + 1 AND #{params.firstIndex} + #{params.recordCountPerPage} ORDER BY TB.USER_UPDATE DESC

SELECT * FROM ( SELECT a.EMPID, a.EMPNO, a.EMPNAME, a.DEPTNAME, a.DEPTCODE, a.EMPGRAD, a.EMPGRADCODE, a.EMPJIKW, a.EMPJIKWCODE, a.EMPHANDPHONE, a.EMPTELPHONE, a.EMPMAIL, a.AUTHOR_CODE, a.COM_CODE, a.EMP_STATE, CASE a.AUTHOR_CODE WHEN 'ROLE_USER' THEN (SELECT COM_PLAY_FLOOR FROM TB_COMPANYINFO c WHERE a.COM_CODE = c.COM_CODE) ELSE '' END as FLOOR_INFO FROM TB_EMPINFO a, TB_USERINFO b WHERE a.EMPNO = b.USER_NO AND a.COM_CODE = b.COM_CODE
AND b.USER_ID = #{params.userId}
AND b.USER_NO = #{params.userNo}
AND b.USER_PASSWORD = #{params.userPassword}
) X

mapper.update:
        UPDATE  TB_USERINFO SET USER_ID = #{userId,jdbcType=VARCHAR} 
                                , USER_NAME = #{userName} 

                                , COM_CODE = #{comCode}


                                , USER_RANK = #{userRank,jdbcType=VARCHAR}


                                , USER_POSITION = #{userPosition,jdbcType=VARCHAR} 

                                , USER_EMAIL = #{userEmail,jdbcType=VARCHAR} 

                                , USER_STATE = #{userState,jdbcType=VARCHAR} 

                                , USER_CELLPHONE = #{userCellphone,jdbcType=VARCHAR} 
                                , USER_TEL = #{userTel,jdbcType=VARCHAR} 
                                , USER_UPDATE = GETDATE()
                                , USER_UPDATEID = #{updateId,jdbcType=VARCHAR}

                                , USER_PASSWORD = #{userPassword,jdbcType=VARCHAR}

	    WHERE USER_NO = #{userNo} ;

	    UPDATE TB_EMPINFO SET  EMPNAME = #{userName}  

                                , EMPID = #{userId,jdbcType=VARCHAR} 


                                , COM_CODE = #{comCode}


                                , EMPGRAD = #{userRank,jdbcType=VARCHAR}


                                , EMPJIKW = #{userPosition,jdbcType=VARCHAR} 

                                , EMPMAIL = #{userEmail,jdbcType=VARCHAR} 

                                , EMP_STATE = #{userState,jdbcType=VARCHAR} 

                                , EMPHANDPHONE = #{userCellphone,jdbcType=VARCHAR} 
                                , EMPTELPHONE = #{userTel,jdbcType=VARCHAR}
                                , UPDATE_DATE = GETDATE()
	    WHERE EMPNO = #{userNo} ;

{ call sp_UserPassChange(#{userNo ,mode=IN, jdbcType=VARCHAR, javaType=string}
                         , #{nowPassword ,mode=IN, jdbcType=VARCHAR, javaType=string}
                         , #{newPassword ,mode=IN, jdbcType=VARCHAR, javaType=string}
                         , #{result ,mode=OUT, jdbcType=INTEGER, javaType=int} )   
}
