mapper.select:
SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, TA.* FROM ( SELECT TB.EMPID, TB.EMPNO, TB.EMPNAME, TB.DEPTNAME, TB.DEPTCODE, TB.EMPGRAD, TB.EMPGRADCODE, TB.EMPJIKW, TB.EMPJIKWCODE, TB.EMPHANDPHONE, TB.EMPTELPHONE, TB.EMPMAIL, TB.ADMIN_GUBUN FROM (SELECT a.EMPID, a.EMPNO, a.EMPNAME, a.DEPTNAME, a.DEPTCODE, a.EMPGRAD, a.EMPGRADCODE, a.EMPJIKW, a.EMPJIKWCODE, a.EMPHANDPHONE, a.EMPTELPHONE, a.EMPMAIL, a.ADMIN_GUBUN FROM TB_EMPINFO a WHERE AUTHOR_CODE != 'ROLE_USER'
AND ( a.EMPNAME LIKE CONCAT('%',#{params.searchKeyword},'%') OR a.EMPNO LIKE CONCAT('%',#{params.searchKeyword},'%') )
AND a.DEPTNAME LIKE CONCAT('%',#{params.searchKeyword},'%')
AND a.AVAYA_USERID is null and a.EMPMAIL is not null
AND a.DEPTCODE= #{params.searchJobpst}
AND a.EMPID IN (SELECT ADMIN_ID FROM tb_admin WHERE ADMIN_LEVEL = 'ROLE_ANONYMOUS')
)TB )TA ORDER BY TA.EMPNAME ASC
LIMIT ${params.firstIndex}, ${params.recordCountPerPage}

insert.selectKey:
SELECT IFNULL(COUNT(EMPNO),0)  as cnt
FROM TB_EMPINFO 
WHERE EMPNO = #{empno}
