mapper.select:
SELECT * FROM ( SELECT a.EMPID, a.EMPNO, a.EMPNAME, a.DEPTNAME, a.DEPTCODE, a.EMPGRAD, a.EMPGRADCODE, a.EMPJIKW, a.EMPJIKWCODE, a.EMPHANDPHONE, a.EMPTELPHONE, a.EMPMAIL, a.AUTHOR_CODE, a.COM_CODE, a.EMP_STATE, CASE a.AUTHOR_CODE WHEN 'ROLE_USER' THEN (SELECT COM_PLAY_FLOOR FROM TB_COMPANYINFO c WHERE a.COM_CODE = c.COM_CODE) ELSE '' END as FLOOR_INFO FROM TB_EMPINFO a, TB_USERINFO b WHERE a.EMPNO = b.USER_NO AND a.COM_CODE = b.COM_CODE
AND b.USER_ID = #{params.userId}
AND b.USER_NO = #{params.userNo}
AND b.USER_PASSWORD = #{params.userPassword}
) X

insert.selectKey:
SELECT ISNULL(COUNT(EMPNO),0)  as cnt
FROM TB_EMPINFO 
WHERE EMPNO = #{empno}
