mapper.select:
SELECT TA.* FROM (SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, ROW_NUMBER() OVER (ORDER BY TB.VISITED_REGDATE DESC) AS RNUM, TB.* FROM (SELECT DISTINCT a.VISITED_CODE, a.VISITED_STATUS, dbo.FN_DAYCONVERT(a.VISITED_RESDAY) VISITED_RESDAY, dbo.fn_TimeSplit(a.VISITED_RESTIME) VISITED_RESTIME, a.VISITED_PERSON, a.VISITED_PURPOSE, a.VISITED_GUBUN, a.VISITED_QRCODE, a.CENTER_ID, a.FLOOR_SEQ, a.VISITED_EMPNO, a.VISITED_REGDATE, CONVERT(varchar(10),a.VISITED_REGDATE,120) VISITEDREGDATE , a.VISITED_REGID, a.VISITED_UPDATEDATE, a.VISITED_UPDATEID, CAST(a.VISITED_REMARK as varchar(4000)) VISITED_REMARK , e.CODE_NM, a.VISITED_REQ_NAME, a.VISITED_REQ_CELPHONE, a.VISITED_REQ_ORG, e.CODE, a.VISITED_GROUP_NAME, a.VISITED_GROUP_CHECK, a.CANCEL_REASON
, b.CENTER_NM, c.FLOOR_NAME, d.EMPNAME, d.EMPHANDPHONE
FROM TB_VISITEDINFO a, LETTCCMMNDETAILCODE e
, TB_CENTERINFO b, TB_FLOORINFO c , TB_EMPINFO d
WHERE a.VISITED_GUBUN = #{params.visitedGubun} AND a.VISITED_STATUS = e.CODE
AND a.CENTER_ID = b.CENTER_ID AND a.FLOOR_SEQ = c.FLOOR_SEQ AND a.VISITED_EMPNO = d.EMPNO
AND CONVERT(varchar(8), A.VISITED_REGDATE, 112) >= #{params.searchStartDay} AND CONVERT(varchar(8), A.VISITED_REGDATE, 112) < CONVERT (VARCHAR(8), DATEADD(DAY,1,CONVERT(DATE,#{params.searchEndDay})), 112)
AND a.VISITED_RESDAY BETWEEN #{params.searchStartDay} and #{params.searchEndDay}
AND d.EMPNO = #{params.empno}
AND a.VISITED_CODE = #{params.visitedCode}
AND a.VISITED_REQ_NAME = #{params.seachName} AND a.VISITED_REQ_CELPHONE= #{params.seachCelphone}
AND a.VISITED_STATUS IN ('VISITED_STATE_1', 'VISITED_STATE_2', 'VISITED_STATE_3')
AND a.VISITED_STATUS IN ('VISITED_STATE_1', 'VISITED_STATE_2', 'VISITED_STATE_3', 'VISITED_STATE_4', 'VISITED_STATE_6')
) TB ) TA WHERE RNUM BETWEEN #{params.firstIndex} + 1 AND #{params.firstIndex} + #{params.recordCountPerPage} ORDER BY TA.VISITED_REGDATE DESC

{ call SP_VISITEDINFO(#{visitedGubun ,mode=IN, jdbcType=VARCHAR, javaType=string} , 
                      #{visitedCode ,mode=IN, jdbcType=VARCHAR, javaType=string}
                      )
}

       SELECT b.TOUR_ALLOW_PERSON,
	         (SELECT ISNULL(SUM(VISITED_PERSON),0)
			 FROM TB_VISITEDINFO
			 WHERE VISITED_RESDAY = #{params.visitedResday}
                  AND VISITED_RESTIME = #{params.visitedRestime}
				  ) TOUR_PERSON
	   FROM TB_SWCINFO b

mapper.insert:
SELECT dbo.FN_VISITEDCODE(#{visitedGubun}) as visitedCode
INSERT INTO TB_VISITEDINFO (VISITED_CODE, VISITED_STATUS, VISITED_RESDAY, VISITED_RESTIME, VISITED_PERSON, VISITED_PURPOSE, VISITED_GUBUN, VISITED_QRCODE, CENTER_ID, FLOOR_SEQ, VISITED_EMPNO, VISITED_REMARK, VISITED_REGDATE, VISITED_REGID, VISITED_UPDATEDATE, VISITED_UPDATEID, VISITED_REQ_NAME, VISITED_REQ_CELPHONE, VISITED_REQ_ORG, VISITED_GROUP_NAME, VISITED_GROUP_CHECK ) VALUES (#{visitedCode} , #{visitedStatus}, #{visitedResday}, #{visitedRestime}, #{visitedPerson , jdbcType=VARCHAR}, #{visitedPurpose , jdbcType=VARCHAR}, #{visitedGubun}, #{visitedQrcode , jdbcType=VARCHAR}, #{centerId}, #{floorSeq}, #{visitedEmpno}, #{visitedRemark , jdbcType=VARCHAR}, getdate(), #{visitedRegid, jdbcType=VARCHAR}, getdate(), #{visitedUpdateid, jdbcType=VARCHAR}, #{visitedReqName, jdbcType=VARCHAR} , #{visitedReqCelphone, jdbcType=VARCHAR} , #{visitedReqOrg, jdbcType=VARCHAR} , #{visitedGroupName, jdbcType=VARCHAR} , #{visitedGroupCheck, jdbcType=VARCHAR} );
INSERT INTO TB_VISITEDDETAIL(VISITED_CODE, VISITED_ORG, VISITED_NAME, VISITED_CELPHONE, VISITED_REGDATE) VALUES (#{visitedCode}, #{detailList.visitedOrg}, #{detailList.visitedName}, #{detailList.visitedCelphone}, getdate())

update.foreach:

UPDATE TB_VISITEDDETAIL	 SET VISITED_QRCODE = #{item.visitedQrcode ,jdbcType=VARCHAR}
                             , VISITED_QRCODE_CK = 'N'
WHERE VISITED_SEQ = #{item.visitedSeq}

mapper.update:
UPDATE TB_VISITEDINFO SET VISITED_STATUS = #{visitedStatus}

                          , VISITED_QRCODE = #{visitedQrcode} 

                          , VISITED_UPDATEDATE = getdate()
                          , VISITED_UPDATEID =  #{visitedUpdateid , jdbcType=VARCHAR}

                          , CANCEL_REASON = #{cancelReason} 

WHERE VISITED_CODE = #{visitedCode}
