mapper.select:
SELECT TB.* FROM ( SELECT b.FLOOR_SEQ, b.FLOOR_NAME, SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, ROW_NUMBER() OVER (ORDER BY b.FLOOR_SEQ DESC) AS RNUM, b.FLOOR_INFO, a.CENTER_ID , a.CENTER_NM , CASE WHEN LEN(FLOOR_MAP) > 0 THEN FLOOR_MAP ELSE 'no_image.gif' END FLOOR_MAP, dbo.FN_DETAILCODENM(b.FLOOR_INFO) FLOOR_INFO_TXT, SEAT_CNT, MEET_CNT, FLOOR_USEYN, FLOOR_PART, FORMAT(b.UPDATE_DATE,'yyyy-MM-dd HH:mm:ss') UPDATE_DATE, b.UPDATE_ID FROM TB_CENTERINFO a, TB_FLOORINFO b WHERE a.CENTER_ID = b.CENTER_ID
AND ( b.FLOOR_NAME LIKE CONCAT('%',#{params.searchKeyword},'%') OR a.CENTER_NM LIKE CONCAT('%',#{params.searchKeyword},'%') )
AND a.CENTER_ID = #{params.centerId}
AND b.FLOOR_USEYN = #{params.floorUseyn}
AND FLOOR_SEQ in (SELECT strValue FROM dbo.UF_SPLICT( #{params.floorInfo}) )
AND FLOOR_SEQ IN (SELECT FLOOR_SEQ FROM TB_SEATINFO WHERE SEAT_USEYN ='Y' GROUP BY FLOOR_SEQ)
AND FLOOR_SEQ IN (SELECT FLOOR_SEQ FROM TB_MEETING_ROOM WHERE MEETING_USEYN ='Y' AND ROOM_TYPE IN('SWC_GUBUN_1', 'SWC_GUBUN_2') GROUP by FLOOR_SEQ)
AND FLOOR_SEQ IN (SELECT FLOOR_SEQ FROM TB_MEETING_ROOM WHERE MEETING_USEYN ='Y' AND ROOM_TYPE = 'SWC_GUBUN_3' GROUP by FLOOR_SEQ)
) TB
WHERE RNUM BETWEEN #{params.firstIndex} + 1 AND #{params.firstIndex} + #{params.recordCountPerPage}
ORDER BY CAST( replace( TB.FLOOR_INFO, 'CENTER_FLOOR_','') as int) ASC , TB.FLOOR_SEQ DESC

mapper.update:

 UPDATE  TB_FLOORINFO SET CENTER_ID = #{centerId} 
                          , FLOOR_INFO = #{floorInfo,jdbcType=VARCHAR} 
                          , FLOOR_NAME = #{floorName,jdbcType=VARCHAR}
                          , FLOOR_USEYN = #{floorUseyn,jdbcType=VARCHAR}
                          , FLOOR_PART = #{floorPart,jdbcType=VARCHAR} 

                           , FLOOR_MAP = #{floorMap,jdbcType=VARCHAR}


                           , FLOOR_MAP1 = #{floorMap1,jdbcType=VARCHAR}

                          , UPDATE_DATE = GETDATE()
                          , UPDATE_ID = #{userId,jdbcType=VARCHAR}
WHERE FLOOR_SEQ = #{floorSeq}
