mapper.select:
SELECT TB.* , SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT FROM ( SELECT ROW_NUMBER() OVER (ORDER BY c.PART_SEQ DESC) AS RNUM, c.PART_SEQ, c.PART_NAME, b.FLOOR_SEQ, b.FLOOR_INFO, b.FLOOR_NAME, a.CENTER_ID, a.CENTER_NM , CASE WHEN length(PART_MAP1) > 0 THEN PART_MAP1 ELSE 'no_image.gif' END PART_MAP1, b.FLOOR_PART, c.UPDATE_ID, DATE_FORMAT(c.UPDATE_DATE,'%Y-%m-%d %H:%i:%s') UPDATE_DATE, c.PART_SEATRULE, c.PART_MINI_CSS, c.PART_MINI_TOP, c.PART_MINI_LEFT, c.PART_USEYN, c.PART_ORDER, c.PART_CSS FROM TB_CENTERINFO a, TB_FLOORINFO b, TB_FLOORPART c WHERE a.CENTER_ID = b.CENTER_ID AND b.FLOOR_SEQ = c.FLOOR_SEQ
AND ( b.FLOOR_NAME LIKE CONCAT('%',#{params.searchKeyword},'%') OR a.CENTER_NM LIKE CONCAT('%',#{params.searchKeyword},'%') )
AND b.FLOOR_SEQ = #{params.floorSeq}
AND b.CENTER_ID = #{params.centerId}
AND c.PART_USEYN = #{params.partUseyn}
) TB WHERE RNUM BETWEEN #{params.firstIndex} + 1 AND #{params.firstIndex} + #{params.recordCountPerPage} ORDER BY TB.PART_ORDER asc , TB.PART_SEQ DESC

SELECT  PART_SEQ , PART_NAME 
FROM TB_FLOORPART
WHERE PART_USEYN = 'Y'

      AND CENTER_ID = #{centerId}


      AND FLOOR_SEQ = #{floorSeq}

ORDER BY  PART_SEQ  ASC

mapper.update:
         UPDATE  TB_FLOORPART SET CENTER_ID = #{centerId} 
	                              , FLOOR_SEQ = #{floorSeq, jdbcType=VARCHAR} 
	                              , PART_CSS = #{partCss,jdbcType=VARCHAR}
	                              , PART_NAME = #{partName,jdbcType=VARCHAR}
	                              , PART_SEATRULE = #{partSeatrule,jdbcType=VARCHAR} 
	                              , PART_MINI_CSS = #{partMiniCss,jdbcType=VARCHAR} 
	                              , PART_MINI_TOP = #{partMiniTop,jdbcType=VARCHAR} 
	                              , PART_MINI_LEFT = #{partMiniLeft,jdbcType=VARCHAR} 
	                              , PART_USEYN = #{partUseyn,jdbcType=VARCHAR} 
	                              , PART_ORDER = #{partOrder,jdbcType=VARCHAR} 

                                    , PART_MAP1 = #{partMap1,jdbcType=VARCHAR}


                                    , FLOOR_MAP2 = #{partMap2,jdbcType=VARCHAR}

	                              , UPDATE_DATE = GETDATE()
	                              , UPDATE_ID = #{userId,jdbcType=VARCHAR}
	     WHERE PART_SEQ = #{partSeq}
