mapper.select:
SELECT CAST( SUM(1) OVER(PARTITION BY NULL) AS INTEGER ) AS TOTAL_RECORD_COUNT, TA.* FROM ( SELECT TB.* FROM ( SELECT a.EQUP_CODE , a.EQUIPMENT_ID, a.EQUIPMENT_NAME , (SELECT CENTER_NM FROM tb_centerinfo WHERE center_id=a.CENTER_ID) centerNm , a.REG_DATE, a.COMPANY, a.REMARK , a.USE_YN , a.EQUIP_STATE , CASE a.EQUIP_STATE WHEN 'EQUIP_STATE_2' THEN DATE_FORMAT(a.EQUP_INDATE, '%Y-%m-%d') ELSE '' END as EQUP_INDATE,
CASE a.EQUIP_STATE WHEN 'EQUIP_STATE_2' THEN DATE_FORMAT(a.EQUP_OTDATE, '%Y-%m-%d') ELSE '' END AS EQUP_OTDATE , fn_DetailCodeNm(a.EQUIP_STATE) EQUIP_STATE_TXT FROM TB_EQUIPMENTINFO a WHERE 1=1
AND ( COMPANY LIKE CONCAT('%',#{searchKeyword},'%') OR EQUIPMENT_NAME LIKE CONCAT('%',#{searchKeyword},'%') OR )
AND SWC_SEQ LIKE CONCAT('%',#{searchKeyword},'%')
AND EQUIPMENT_NAME LIKE CONCAT('%',#{searchKeyword},'%')
AND COMPANY LIKE CONCAT('%',#{searchKeyword},'%')
AND CENTER_ID = #{searchcenterId}
AND EQUIP_STATE = #{searchEquipState}
ORDER BY a.EQUP_CODE DESC ) TB
)TA ORDER BY TA.EQUP_CODE DESC LIMIT #{firstIndex}, #{recordCountPerPage}

SELECT IFNULL(COUNT(*), 0) FROM TB_EQUIPMENTINFO a WHERE a.EQUP_CODE IN
#{equpList}
AND EQUIP_STATE != #{equipState}

mapper.insert:
	      INSERT INTO TB_EQUIPMENTINFO( EQUP_CODE
     														,EQUIPMENT_ID
     														,EQUIPMENT_NAME
     														, CENTER_ID
     														, REG_DATE
     														, COMPANY
     														, REMARK
     														, USE_YN
     														, EQUIP_SERIAL
     														, EQUIP_STATE)
      	VALUES (	#{equpCode}
	      				, #{equipmentId,  jdbcType=VARCHAR}
	      				, #{equipmentName, jdbcType=VARCHAR}
	      				, #{centerId}
	      				, NOW()
	      				, #{company ,jdbcType=VARCHAR}
	      				, #{remark ,jdbcType=VARCHAR}
	      				, #{useYn ,jdbcType=VARCHAR}
	      				, #{equipSerial ,jdbcType=VARCHAR}
	      				, 'EQUIP_STATE_1'
      				)

mapper.update:
UPDATE TB_EQUIPMENTINFO SET EQUIP_STATE = #{equipState}
                                            , SWC_SEQ = #{swcSeq}
                                            , USER_ID = #{userId}

                                            , EQUP_INDATE = now()


                                            , EQUP_OTDATE = now()


                                            , EQUIP_SERIAL = #{equipSerial,jdbcType=VARCHAR}

 WHERE EQUP_CODE IN

 #{equpList}

UPDATE TB_EQUIPMENTINFO SET EQUIPMENT_NAME = #{equipmentName}
                       ,  EQUIPMENT_ID = #{equipmentId,jdbcType=VARCHAR }
                       ,  CENTER_ID = #{centerId}
                       ,  COMPANY = #{company, jdbcType=VARCHAR }
                       ,  REMARK = #{remark, jdbcType=VARCHAR }
                       ,  USE_YN = #{useYn, jdbcType=VARCHAR }
WHERE EQUP_CODE = #{equpCode}
