mapper.select:
SELECT TB.* FROM ( SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, ROW_NUMBER() OVER (ORDER BY a.SEAT_ORDER DESC) AS RNUM, a.CENTER_ID, a.FLOOR_SEQ, a.PART_SEQ, a.SEAT_ID, a.SEAT_NAME, a.SEAT_USEYN, a.SEAT_TOP, a.SEAT_LEFT, a.SEAT_FIX_USERYN, a.SEAT_FIX_USER_ID, a.ORG_CD, a.SEAT_QR_CODE, a.SEAT_QR_CODE_PATH, a.SEAT_QR_CODE_FULL_PATH, a.SEAT_ORDER, a.SEAT_NUMBER, FORMAT(a.UPDATE_DATE,'yyyy-MM-dd HH:mm:ss') UPDATE_DATE, b.CENTER_NM, c.FLOOR_NAME, a.UPDATE_ID, d.CODE_NM , dbo.FN_DETAILCODENM(a.PAY_CLASSIFICATION) AS PAY_CLASSIFICATION_TXT, a.PAY_CLASSIFICATION, dbo.FN_DETAILCODENM(a.PAY_GUBUN) AS PAY_GUBUN_TXT, a.PAY_GUBUN, a.PAY_COST, c.FLOOR_INFO, a.RES_REQDAY, CASE WHEN (SEAT_FIX_USERYN = 'Y' AND LEN(SEAT_FIX_USER_ID) > 0) THEN dbo.FN_USERNM(a.SEAT_FIX_USER_ID) ELSE '' END USER_NAME, CASE a.SEAT_CONFIRMGUBUN WHEN 'Y' THEN '관리자승인' ELSE '바로사용' END SEAT_CONFIRMGUBUN, QR_PLAYYN, a.SEAT_LABEL_USEYN, a.SEAT_LABEL_STATUS, a.SEAT_LABEL_CODE, a.SEAT_LABEL_TEMPLATE, dbo.FN_DETAILCODENM(a.SEAT_LABEL_TEMPLATE) LABEL_TEMPLATE_TXT, a.SEAT_FIX_GUBUN FROM TB_SEATINFO a, TB_CENTERINFO b, TB_FLOORINFO c, LETTCCMMNDETAILCODE d WHERE a.CENTER_ID = b.CENTER_ID AND a.FLOOR_SEQ = c.FLOOR_SEQ AND a.SEAT_GUBUN = d.CODE
AND (a.SEAT_NAME LIKE CONCAT('%',#{params.searchKeyword},'%'))
AND a.QR_PLAYYN = #{params.searchQrplay}
AND a.CENTER_ID = #{params.searchCenter}
AND a.FLOOR_SEQ = #{params.searchFloorSeq}
AND a.PART_SEQ = #{params.searchPartSeq}
AND a.SEAT_LABEL_USEYN = #{params.seatLabelUseyn}
) TB WHERE RNUM BETWEEN #{params.firstIndex} + 1 AND #{params.firstIndex} + #{params.recordCountPerPage} ORDER BY CAST(REPLACE(TB.FLOOR_INFO, 'CENTER_FLOOR_','') AS INT) ASC, TB.SEAT_ORDER DESC

mapper.insert:
SELECT dbo.FN_SEATID(#{floorSeq}) as seatId
INSERT INTO TB_SEATINFO(CENTER_ID, FLOOR_SEQ, PART_SEQ, SEAT_ID, SEAT_NAME, SEAT_USEYN, SEAT_TOP, SEAT_LEFT, SEAT_FIX_USERYN, SEAT_FIX_USER_ID, ORG_CD, SEAT_ORDER, SEAT_NUMBER, REG_ID, REG_DATE, UPDATE_ID, UPDATE_DATE, SEAT_GUBUN, PAY_CLASSIFICATION, PAY_GUBUN, PAY_COST, SEAT_CONFIRMGUBUN, SEAT_FIX_GUBUN , RES_REQDAY, QR_PLAYYN, SEAT_LABEL_USEYN, SEAT_LABEL_CODE, SEAT_LABEL_TEMPLATE) VALUES (#{centerId}, #{floorSeq}, #{partSeq,jdbcType=VARCHAR}, #{seatId}, #{seatName}, 'Y', #{seatTop,jdbcType=VARCHAR}, #{seatLeft,jdbcType=VARCHAR}, #{seatFixUseryn, jdbcType=VARCHAR}, #{seatFixUserId, jdbcType=VARCHAR}, #{orgCd,jdbcType=VARCHAR}, #{seatOrder,jdbcType=VARCHAR}, #{seatNumber,jdbcType=VARCHAR}, #{userId,jdbcType=VARCHAR}, GETDATE(), #{userId,jdbcType=VARCHAR}, GETDATE(), #{seatGubun,jdbcType=VARCHAR},#{payClassification,jdbcType=VARCHAR},#{payGubun,jdbcType=VARCHAR},#{payCost,jdbcType=VARCHAR}, #{seatConfirmgubun,jdbcType=VARCHAR} , #{seatFixGubun,jdbcType=VARCHAR} , #{resReqday, jdbcType=VARCHAR} , #{qrPlayyn, jdbcType=VARCHAR} #{seatLabelUseyn,jdbcType=VARCHAR}, #{seatLabelCode,jdbcType=VARCHAR}, #{seatLabelTemplate,jdbcType=VARCHAR} );
UPDATE tb_floorinfo SET SEAT_CNT = SEAT_CNT + 1 WHERE FLOOR_SEQ = #{floorSeq};

choose.when:
INSERT INTO TB_SEATINFO(SEAT_ID, CENTER_ID, FLOOR_SEQ, PART_SEQ, SEAT_NAME, SEAT_USEYN, SEAT_TOP, SEAT_LEFT, SEAT_ORDER, REG_DATE, UPDATE_DATE, REG_ID, UPDATE_ID, SEAT_GUBUN, PAY_CLASSIFICATION, PAY_GUBUN, PAY_COST, SEAT_FIX_USERYN ) SELECT dbo.FN_SEATID_INSERT(TA.FLOOR_SEQ, RNUM) , TA.CENTER_ID , TA.FLOOR_SEQ, TA.PART_SEQ, TA.RN, 'Y', 0, 0, TA.RNUM, GETDATE(), GETDATE(), #{params.userId}, #{params.userId}, #{params.seatGubun},#{params.payClassification},#{params.payGubun},#{params.payCost} , #{params.seatFixUseryn, jdbcType=VARCHAR} FROM (
SELECT a.CENTER_ID , a.FLOOR_SEQ , a.PART_SEQ, CONCAT(a.PART_SEATRULE,'_', a.PART_NAME,'_', REPLICATE('0',4 - LEN( b.rn)) , b.rn) RN, ROW_NUMBER() OVER(ORDER BY b.RN DESC) as RNUM FROM tb_floorpart a, ( select * from dbo.FN_ROWTABLE(${params.seatStr}, ${params.seatEnd} ) )b WHERE PART_SEQ = #{params.partSeq}
)TA ORDER BY TA.RN ASC ;
INSERT INTO TB_SEATINFO(SEAT_ID, CENTER_ID, FLOOR_SEQ, SEAT_NAME, SEAT_USEYN, SEAT_TOP, SEAT_LEFT, SEAT_ORDER, REG_DATE, UPDATE_DATE, REG_ID, UPDATE_ID, SEAT_GUBUN, PAY_CLASSIFICATION, PAY_GUBUN, PAY_COST, SEAT_FIX_USERYN ) SELECT dbo.FN_SEATID_INSERT(TA.FLOOR_SEQ, RNUM) , TA.CENTER_ID , TA.FLOOR_SEQ, TA.RN, 'Y', 0,0 , TA.RNUM, GETDATE(), GETDATE(), #{params.userId}, #{params.userId}, #{params.seatGubun},#{params.payClassification},#{params.payGubun},#{params.payCost}, #{params.seatFixUseryn, jdbcType=VARCHAR} FROM (
SELECT a.CENTER_ID , a.FLOOR_SEQ , 0 PART_SEQ, CONCAT(a.FLOOR_NAME, '_', REPLICATE('0',4 - LEN( b.rn)) , b.rn) RN, ROW_NUMBER() OVER(ORDER BY b.RN DESC) as RNUM FROM tb_floorinfo a, ( select * from dbo.FN_ROWTABLE(${params.seatStr}, ${params.seatEnd} ) )b WHERE FLOOR_SEQ = #{params.floorSeq}
)TA ORDER BY TA.RN ASC ;
UPDATE TB_FLOORINFO SET SEAT_CNT = (SELECT ISNULL(count(*),0) FROM tb_seatinfo WHERE FLOOR_SEQ = #{params.floorSeq} ) WHERE FLOOR_SEQ = #{params.floorSeq};

INSERT INTO TB_MEETING_ROOM(MEETING_ID, CENTER_ID, FLOOR_SEQ, PART_SEQ, MEETING_NAME, MEETING_USEYN, MEETING_TOP, MEETING_LEFT, MEETING_ORDER, REG_DATE, UPDATE_DATE, REG_ID, UPDATE_ID, MEETING_MAINVIEW, MEETING_VIEW, MAX_CNT, MAIL_SENDCHECK, SMS_SENDCHECK, ROOM_TYPE, MEETING_CONFIRMGUBUN, PAY_CLASSIFICATION, PAY_GUBUN, PAY_COST ) SELECT dbo.FN_MEETINGID_INSERT(TA.FLOOR_SEQ, RNUM) , TA.CENTER_ID , TA.FLOOR_SEQ, TA.PART_SEQ, TA.RN, 'Y', 0, 0, TA.RNUM, GETDATE(), GETDATE(), #{params.userId}, #{params.userId}, 'Y','Y',0, 'N','N', #{params.roomType}, #{params.meetingConfirmgubun}, #{params.payClassification},#{params.payGubun},#{params.payCost} FROM (
SELECT a.CENTER_ID , a.FLOOR_SEQ , a.PART_SEQ, CONCAT(''M_', a.PART_SEATRULE,'_', a.PART_NAME,'_', REPLICATE('0',4 - LEN( b.rn)) , b.rn) RN, ROW_NUMBER() OVER(ORDER BY b.RN DESC) as RNUM FROM tb_floorpart a, ( select * from dbo.FN_ROWTABLE(${params.seatStr}, ${params.seatEnd} ) )b WHERE PART_SEQ = #{params.partSeq}
)TA ORDER BY TA.RN ASC ;
INSERT INTO TB_MEETING_ROOM(MEETING_ID, CENTER_ID, FLOOR_SEQ, PART_SEQ, MEETING_NAME, MEETING_USEYN, MEETING_TOP, MEETING_LEFT, MEETING_ORDER, REG_DATE, UPDATE_DATE, REG_ID, UPDATE_ID, MEETING_MAINVIEW, MEETING_VIEW, MAX_CNT, MAIL_SENDCHECK, SMS_SENDCHECK, ROOM_TYPE, MEETING_CONFIRMGUBUN, PAY_CLASSIFICATION, PAY_GUBUN, PAY_COST ) SELECT dbo.FN_MEETINGID_INSERT(TA.FLOOR_SEQ, RNUM) , TA.CENTER_ID , TA.FLOOR_SEQ, TA.PART_SEQ, TA.RN, 'Y', 0, 0, TA.RNUM, GETDATE(), GETDATE(), #{params.userId}, #{params.userId}, 'Y','Y',0, 'N','N', #{params.roomType}, #{params.meetingConfirmgubun}, #{params.payClassification},#{params.payGubun},#{params.payCost} FROM (
SELECT a.CENTER_ID , a.FLOOR_SEQ , 0 PART_SEQ, CONCAT('M_',a.FLOOR_NAME, '_', REPLICATE('0',4 - LEN( b.rn)) , b.rn) RN, ROW_NUMBER() OVER(ORDER BY b.RN DESC) as RNUM FROM TB_FLOORINFO a, ( SELECT * FROM dbo.FN_ROWTABLE(${params.seatStr}, ${params.seatEnd} ) )b WHERE FLOOR_SEQ = #{params.floorSeq}
)TA ORDER BY TA.RN ASC ;
UPDATE tb_floorinfo SET MEET_CNT = (SELECT ISNULL(count(*),0) FROM tb_meeting_room WHERE FLOOR_SEQ = #{params.floorSeq} ) WHERE FLOOR_SEQ = #{params.floorSeq};

mapper.update:
        UPDATE TB_SEATINFO SET CENTER_ID = #{centerId} 
	                              , FLOOR_SEQ = #{floorSeq,jdbcType=VARCHAR} 
	                              , PART_SEQ = #{partSeq,jdbcType=VARCHAR} 
	                              , SEAT_NAME = #{seatName}
	                              , SEAT_USEYN = #{seatUseyn, jdbcType=VARCHAR}
	                              , SEAT_TOP = #{seatTop,jdbcType=VARCHAR}
	                              , SEAT_LEFT = #{seatLeft,jdbcType=VARCHAR}
	                              , SEAT_FIX_USERYN = #{seatFixUseryn, jdbcType=VARCHAR}
	                              , SEAT_FIX_USER_ID = #{seatFixUserId, jdbcType=VARCHAR}
	                              , ORG_CD = #{orgCd,jdbcType=VARCHAR} 
	                              , SEAT_ORDER  = #{seatOrder,jdbcType=VARCHAR} 
	                              , SEAT_NUMBER = #{seatNumber,jdbcType=VARCHAR} 
	                              , UPDATE_DATE = GETDATE()
	                              , UPDATE_ID = #{userId,jdbcType=VARCHAR}
	                              , SEAT_GUBUN = #{seatGubun,jdbcType=VARCHAR}
	                              , PAY_CLASSIFICATION = #{payClassification,jdbcType=VARCHAR}
	                              , PAY_GUBUN = #{payGubun,jdbcType=VARCHAR}
	                              , PAY_COST = #{payCost,jdbcType=VARCHAR}
	                              , SEAT_CONFIRMGUBUN = #{seatConfirmgubun,jdbcType=VARCHAR}
	                              , SEAT_FIX_GUBUN = #{seatFixGubun,jdbcType=VARCHAR}
	                              , RES_REQDAY = #{resReqday, jdbcType=VARCHAR}
	                              , SEAT_LABEL_USEYN = #{seatLabelUseyn}
	                              , QR_PLAYYN  = #{qrPlayyn, jdbcType=VARCHAR}
	                              , SEAT_LABEL_CODE  =  #{seatLabelCode}
	                              , SEAT_LABEL_TEMPLATE = #{seatLabelTemplate}
	    WHERE SEAT_ID = #{seatId}

UPDATE TB_SEATINFO SET SEAT_LABEL_STATUS = #{seatLabelStatus}
                       , SEAT_LABEL_DATE = getdate()
WHERE SEAT_ID = #{seatId}

when.foreach:
        UPDATE TB_SEATINFO SET SEAT_TOP = #{item.seatTop,jdbcType=VARCHAR}
	                           , SEAT_LEFT = #{item.seatLeft,jdbcType=VARCHAR}
	                           , UPDATE_DATE = GETDATE()
	                           , UPDATE_ID = #{item.userId,jdbcType=VARCHAR}
	    WHERE SEAT_ID = #{item.seatId}

otherwise.foreach:
            UPDATE TB_MEETING_ROOM SET MEETING_TOP = #{item.seatTop,jdbcType=VARCHAR}
			                           , MEETING_LEFT = #{item.seatLeft,jdbcType=VARCHAR}
			                           , UPDATE_DATE = GETDATE()
			                           , UPDATE_ID = #{item.userId,jdbcType=VARCHAR}
		    WHERE MEETING_ID = #{item.seatId}

mapper.delete:
        UPDATE TB_FLOORINFO SET SEAT_CNT = SEAT_CNT - b.CNT
        from TB_FLOORINFO a , (SELECT COUNT(*) CNT , FLOOR_SEQ 
						       FROM TB_SEATINFO
						       WHERE SEAT_ID IN 

						            #{item}

						       GROUP  by FLOOR_SEQ) b
        WHERE a.FLOOR_SEQ = b.FLOOR_SEQ;

        DELETE FROM TB_SEATINFO WHERE SEAT_ID IN

            #{item}

        ;
