mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY TB.REG_DATE DESC) AS RNUM, TB.*, SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT FROM ( SELECT a.ADMIN_ID, a.ADMIN_NAME, a.ADMIN_TEL, a.ADMIN_EMAIL, a.DEPT_ID, a.DEPT_NAME, a.ADMIN_PASSWORD, a.LOCK_YN, a.USE_YN, CONVERT(varchar(10), a.REG_DATE, 120) REG_DATE, c.AUTHOR_NM FROM tb_admin a , lettnauthorinfo c WHERE a.AUTHOR_CODE = c.AUTHOR_CODE
and ( ADMIN_ID LIKE CONCAT('%',#{searchKeyword},'%') OR ADMIN_NAME LIKE CONCAT('%',#{searchKeyword},'%') )
AND ADMIN_ID LIKE CONCAT('%',#{searchKeyword},'%')
AND ADMIN_NAME LIKE CONCAT('%',#{searchKeyword},'%')
AND a.USE_YN = #{params.useYn}
) TB ) TA WHERE RNUM BETWEEN #{params.firstIndex} + 1 AND #{params.firstIndex} + #{params.recordCountPerPage} ORDER BY TA.REG_DATE DESC

mapper.insert:
INSERT INTO tb_admin( ADMIN_ID, ADMIN_PASSWORD, ADMIN_NAME, USE_YN, DEPT_ID, DEPT_NAME, ADMIN_EMAIL, 
                                ADMIN_TEL, REG_DATE, UPDATE_PASSWORD, AUTHOR_CODE, LOCK_YN, EMP_NO)
VALUES (	#{adminId}	, #{adminPassword} , #{adminName}, #{useYn}, #{deptId,jdbcType=VARCHAR }, #{deptName}, #{adminEmail ,jdbcType=VARCHAR},
#{adminTel ,jdbcType=VARCHAR}, GETDATE(), GETDATE(), #{authorCode ,jdbcType=VARCHAR}, 'N' , #{empNo ,jdbcType=VARCHAR}  );

UPDATE tb_empInfo SET ADMIN_GUBUN = 'ADMIN' WHERE EMPID = #{adminId}

mapper.update:
	   UPDATE tb_admin SET ADMIN_NAME = #{adminName}
                            ,  USE_YN = #{useYn}
                            ,  DEPT_ID = #{deptId, jdbcType=VARCHAR }
                            ,  DEPT_NAME = #{deptName}
                            ,  ADMIN_EMAIL = #{adminEmail, jdbcType=VARCHAR }
                            ,  ADMIN_TEL = #{adminTel, jdbcType=VARCHAR }
                            ,  AUTHOR_CODE = #{authorCode , jdbcType=VARCHAR }


                            , ADMIN_PASSWORD = #{adminPassword} 
                            , UPDATE_PASSWORD = GETDATE()
                            , LOCK_YN ='N'


                            ,  EMP_NO = #{empNo, jdbcType=VARCHAR }

	   WHERE ADMIN_ID = #{adminId}

mapper.delete:
	   DELETE FROM tb_admin
       WHERE ADMIN_ID = #{adminId}   ;
	   UPDATE tb_empInfo SET ADMIN_GUBUN = ''  WHERE EMPID  = #{adminId}
